SELECT identifier[0].id AS MBA_ACCOUNT_DB,    identifier[1].id AS PNI_ACCOUNT_DB,    billDelivery[0].channel.`value` AS CHANNEL1_DB,    billDelivery[0].status.`value` AS STATUS1_DB,    billDelivery[0].validFor.`startTimeStamp` AS VALID_FOR_FROM1_DB,    billDelivery[0].validFor.`endTimeStamp` AS VALID_FOR_TO1_DB,    CASE WHEN billDelivery[0].isLegal='true' then 'Y' ELSE 'N' END AS IS_LEGAL1_DB,    billDelivery[0].address AS ADDRESS1_DB,    billDelivery[0].addressType.`value` AS ADDRESSTYPE1_DB,    CASE WHEN billDelivery[0].purpose.`value`='PROD' then NULL ELSE billDelivery[0].purpose.`value` END AS PURPOSE1_DB  FROM `Core` WHERE META().id LIKE 'BA-%' and billDelivery[0].channel.`value` ='PAPER' and billDelivery[0].status.`value` ='ACTIVE'  LIMIT 1